declare namespace CycleDiscordGoogleApi {
  interface Response {
    body: {
      items: any[]
    }
  }
}

declare module 'cycle-discord-google-api' {
    import {Observable} from 'rx'
    import {DiscordDriver, Response} from 'cycle-discord'
    import {Message} from 'discord.js'

    interface Request {
        url: string
    }

    interface Response$ extends Observable<CycleDiscordGoogleApi.Response> {
        request: Request
    }

    interface Props {
        URL: string
        trigger: RegExp
        transform: (from: any) => any
    }

    export interface Sources {
        HTTP: Observable<Response$>
        Discord: DiscordDriver
        props: Observable<Props>
    }

    export interface Sink {
        Discord: Observable<Response>
        HTTP: Observable<any>
    }
}
